    private void botonBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonBuscarActionPerformed
        // TODO add your handling code here:
        DetalleRecurso ventana=new DetalleRecurso();
        if(ControladorRecursos.getInstancia().obtenerRecursosComoString(jTextField1.getText())!=null){
            ventana.consola.setText(ControladorRecursos.getInstancia().obtenerRecursosComoString(jTextField1.getText()));
            ventana.setVisible(true);
            dispose();
        }else{
            JOptionPane.showMessageDialog(null, "No existen recurso con codigo --> "+jTextField1.getText(), "ADVERTENCIA", JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_botonBuscarActionPerformed

