    private void botonGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonGuardarActionPerformed
        // TODO add your handling code here:
        ControladorAmigos co= ControladorAmigos.getInstancia();
        if(campoRut.getText().length()==0 || campoNombre.getText().length()==0 || campoEMail.getText().length()==0 || campoTelefono.getText().length()==0){
            JOptionPane.showMessageDialog(null, "No debe dejar ningun campo en blanco", "ADVERTENCIA", JOptionPane.WARNING_MESSAGE);
        }else{
            co.crearAmigo(campoRut.getText(), campoNombre.getText(), campoEMail.getText(), campoTelefono.getText());
            Main ventana=new Main();
            ventana.setVisible(true);
            dispose();
        }
    }//GEN-LAST:event_botonGuardarActionPerformed

