    private void botonBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonBuscarActionPerformed
        // TODO add your handling code here:
        String ru=campoRut.getText();
        ControladorAmigos con=ControladorAmigos.getInstancia();
        try{
            String [] arr=con.listarAmigo(ru);
            rut.setText(arr[0]);
            nombre.setText(arr[1]);
            mail.setText(arr[2]);
            fono.setText(arr[3]);
            panelsito.updateUI();
        }catch(NullPointerException e){
           JOptionPane.showMessageDialog(null, "El rut ingresado no es valido o el amigo que intenta eliminar ya fue eliminado", "ADVERTENCIA", JOptionPane.WARNING_MESSAGE);

        }
    }//GEN-LAST:event_botonBuscarActionPerformed

    private void botonEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonEliminarActionPerformed
        // TODO add your handling code here:
        String ru=campoRut.getText();
        ControladorAmigos con=ControladorAmigos.getInstancia();
        int resp=JOptionPane.showConfirmDialog(null, "Esta seguro que quiere eliminar a "+ru,"ALERTA", JOptionPane.YES_NO_OPTION);
        if(resp==0){
            con.eliminarAmigo(ru);
            JOptionPane.showMessageDialog(null, "Se ha eliminado al amigo de manera correcta");
        }
    }//GEN-LAST:event_botonEliminarActionPerformed

